import { motion } from 'framer-motion';
import { Calendar, Clock } from 'lucide-react'; // Import icons         

const RecommendedCard = ({ item, index }) => {
  return (
    <motion.div
      className="bg-gray-800 rounded-lg overflow-hidden group cursor-pointer"
      initial={{ opacity: 0, y: 30 }}
      whileInView={{ opacity: 1, y: 0 }}
      transition={{ delay: index * 0.1, duration: 0.6 }}
      whileHover={{ y: -5 }}
      viewport={{ once: true }}
    >
      <div className="relative">
        <img 
          src={item.image} 
          alt={item.title}
          className="w-full h-48 object-cover group-hover:scale-105 transition-transform duration-300"
        />
        <div className="absolute top-3 right-3">
          <span className={`px-2 py-1 text-xs font-semibold rounded ${
            item.status === 'Live' ? 'bg-red-600 text-white' : 
            item.status === 'Upcoming' ? 'bg-blue-600 text-white' : 
            'bg-yellow-600 text-black'
          }`}>
            {item.status}
          </span>
        </div>
      </div>
      
      <div className="p-4">
        <h3 className="text-white font-semibold text-lg mb-2 line-clamp-2">
          {item.title}
        </h3>
        <p className="text-gray-400 text-sm mb-3">{item.subtitle}</p>
        <div className="flex items-center justify-between text-xs text-gray-500">
          <div className="flex items-center">
            <Calendar className="h-4 w-4 mr-1" />
            {item.date}
          </div>
          <div className="flex items-center">
            <Clock className="h-4 w-4 mr-1" />
            {item.time}
          </div>
        </div>
      </div>
    </motion.div>
  );
};

export default RecommendedCard;
