import { View, Text } from 'react'
import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { ChevronDown, Calendar, Users, Trophy, Play, Star, MapPin, Clock } from 'lucide-react';
import RecommendedCard from '../cards/RecommendedCard';
import TournamentCard from '../cards/TournamentCard';
import VideoCard from '../cards/VideoCard.';

// Mock data for tournaments
const mockTournaments = [
  {
    id: 1,
    title: "Mayez Championship 2025",
    type: "Championship",
    date: "June 15-18, 2025",
    location: "Las Vegas, NV",
    prize: "$50,000",
    participants: 128,
    status: "Upcoming",
    image: "https://images.pexels.com/photos/16074/pexels-photo.jpg",
    color: "from-red-600 to-red-800"
  },
  {
    id: 2,
    title: "Elite Pool Masters",
    type: "Masters",
    date: "July 10-12, 2025",
    location: "New York, NY",
    prize: "$35,000",
    participants: 64,
    status: "Registration Open",
    image: "https://images.unsplash.com/photo-1599685315659-bc876da49fe5",
    color: "from-blue-600 to-blue-800"
  },
  {
    id: 3,
    title: "Pro Series Finals",
    type: "Pro Series",
    date: "August 5-7, 2025",
    location: "Chicago, IL",
    prize: "$75,000",
    participants: 32,
    status: "Qualifiers Ongoing",
    image: "https://images.pexels.com/photos/10627147/pexels-photo-10627147.jpeg",
    color: "from-green-600 to-green-800"
  },
  {
    id: 4,
    title: "Women's Championship",
    type: "Women's Pro",
    date: "September 20-22, 2025",
    location: "Miami, FL",
    prize: "$40,000",
    participants: 64,
    status: "Coming Soon",
    image: "https://images.pexels.com/photos/2017868/pexels-photo-2017868.jpeg",
    color: "from-purple-600 to-purple-800"
  },
  {
    id: 5,
    title: "Seniors League Cup",
    type: "Seniors",
    date: "October 15-17, 2025",
    location: "Phoenix, AZ",
    prize: "$25,000",
    participants: 96,
    status: "Registration Open",
    image: "https://images.unsplash.com/photo-1575553939928-d03b21323afe",
    color: "from-orange-600 to-orange-800"
  },
  {
    id: 6,
    title: "Youth Championship",
    type: "Youth",
    date: "November 12-14, 2025",
    location: "Orlando, FL",
    prize: "$15,000",
    participants: 128,
    status: "Registration Open",
    image: "https://images.pexels.com/photos/6253681/pexels-photo-6253681.jpeg",
    color: "from-cyan-600 to-cyan-800"
  }
];

const mockRecommended = [
  {
    id: 1,
    title: "Mayez Pro Championship - Week 4",
    subtitle: "Elite Players Showcase",
    date: "June 25, 2025",
    time: "8:00 PM EST",
    image: "https://images.unsplash.com/photo-1641638066062-6ebb46f56c70",
    status: "Live"
  },
  {
    id: 2,
    title: "Masters Tournament Finals",
    subtitle: "Championship Showdown",
    date: "June 28, 2025",
    time: "7:30 PM EST",
    image: "https://images.pexels.com/photos/6253986/pexels-photo-6253986.jpeg",
    status: "Upcoming"
  },
  {
    id: 3,
    title: "Women's Elite Series - Semi Finals",
    subtitle: "Top 4 Compete",
    date: "June 30, 2025",
    time: "6:00 PM EST",
    image: "https://images.unsplash.com/photo-1556329754-9420aeb663c5",
    status: "Upcoming"
  },
  {
    id: 4,
    title: "Youth Championship Qualifier",
    subtitle: "Next Generation Stars",
    date: "July 2, 2025",
    time: "5:00 PM EST",
    image: "https://images.pexels.com/photos/3007388/pexels-photo-3007388.jpeg",
    status: "Registration"
  }
];

const mockVideos = [
  {
    id: 1,
    title: "Mayez Tournament Highlights - Episode 15",
    duration: "42:30",
    views: "125K",
    image: "https://images.unsplash.com/photo-1627257060697-acfbecf5d9a2",
    youtubeId: "dQw4w9WgXcQ"
  },
  {
    id: 2,
    title: "Pro Series Championship Final",
    duration: "35:45",
    views: "89K",
    image: "https://images.pexels.com/photos/6253979/pexels-photo-6253979.jpeg",
    youtubeId: "dQw4w9WgXcQ"
  },
  {
    id: 3,
    title: "Masters Tournament Best Shots",
    duration: "28:15",
    views: "156K",
    image: "https://images.pexels.com/photos/16074/pexels-photo.jpg",
    youtubeId: "dQw4w9WgXcQ"
  },
  {
    id: 4,
    title: "Behind the Scenes - Tournament Setup",
    duration: "18:20",
    views: "67K",
    image: "https://images.unsplash.com/photo-1599685315659-bc876da49fe5",
    youtubeId: "dQw4w9WgXcQ"
  }
];

// Video Modal Component
const VideoModal = ({ video, onClose }) => {
  useEffect(() => {
    const handleEscape = (e) => {
      if (e.key === 'Escape') onClose();
    };
    document.addEventListener('keydown', handleEscape);
    return () => document.removeEventListener('keydown', handleEscape);
  }, [onClose]);

  return (
    <motion.div
      className="fixed inset-0 bg-black/90 flex items-center justify-center z-50 p-4"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
      onClick={onClose}
    >
      <motion.div
        className="bg-gray-900 rounded-lg overflow-hidden max-w-4xl w-full"
        initial={{ scale: 0.8, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        exit={{ scale: 0.8, opacity: 0 }}
        onClick={(e) => e.stopPropagation()}
      >
        <div className="aspect-video">
          <iframe
            src={`https://www.youtube.com/embed/${video.youtubeId}?autoplay=1`}
            title={video.title}
            className="w-full h-full"
            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
            allowFullScreen
          />
        </div>
        <div className="p-6">
          <h3 className="text-white text-xl font-semibold mb-2">{video.title}</h3>
          <div className="flex items-center text-gray-400">
            <Star className="h-4 w-4 mr-1" />
            {video.views} views
          </div>
        </div>
      </motion.div>
    </motion.div>
  );
};

const Home = () => {
    const [selectedVideo, setSelectedVideo] = useState(null);
    
  return (
    <div>
        {/* Hero Section Component */}
        <section className="relative h-screen flex items-center justify-center overflow-hidden">
        {/* Background Image */}
        <div 
            className="absolute inset-0 bg-cover bg-center bg-no-repeat"
            style={{
            backgroundImage: `url('https://images.pexels.com/photos/16074/pexels-photo.jpg')`
            }}
        >
            <div className="absolute inset-0 bg-black/60"></div>
        </div>

        {/* Content */}
        <div className="relative z-10 text-center px-4 sm:px-6 lg:px-8">
            <motion.div
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            >
            <h1 className="text-5xl md:text-7xl font-bold text-white mb-6 leading-tight">
                PRECISION.<br />
                COMPETITION.<br />
                <span className="text-red-500">CHAMPIONSHIP.</span>
            </h1>
            <motion.p 
                className="text-xl md:text-2xl text-gray-300 mb-8 max-w-3xl mx-auto"
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ delay: 0.5, duration: 0.8 }}
            >
                The Mayez Tournament Organization Organizes<br />
                And Promotes Professional Pool<br />
                Championship Events.
            </motion.p>
            <motion.button
                className="bg-red-600 hover:bg-red-700 text-white px-8 py-4 rounded-lg text-lg font-semibold transition-colors"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                initial={{ opacity: 0, y: 30 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: 0.8, duration: 0.8 }}
            >
                VIEW TOURNAMENTS
            </motion.button>
            </motion.div>
        </div>

        {/* Scroll Indicator */}
        <motion.div 
            className="absolute bottom-8 left-1/2 transform -translate-x-1/2"
            animate={{ y: [0, 10, 0] }}
            transition={{ repeat: Infinity, duration: 2 }}
        >
            <ChevronDown className="text-white h-8 w-8" />
        </motion.div>
        </section>

        {/* // Recommended Section Component */}
        <section className="py-16 bg-gray-900">
              <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                <motion.h2 
                  className="text-4xl font-bold text-white text-center mb-12"
                  initial={{ opacity: 0, y: 30 }}
                  whileInView={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.6 }}
                  viewport={{ once: true }}
                >
                  RECOMMENDED
                </motion.h2>
        
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
                  {mockRecommended.map((item, index) => (
                    <RecommendedCard key={item.id} item={item} index={index} />
                  ))}
                </div>
              </div>
        </section>

        {/* Tournaments Section Component */}
        <section className="py-16 bg-black">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <motion.h2 
            className="text-4xl font-bold text-white text-center mb-12"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
            >
            TOURNAMENTS
            </motion.h2>

            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {mockTournaments.map((tournament, index) => (
                <TournamentCard key={tournament.id} tournament={tournament} index={index} />
            ))}
            </div>
        </div>
        </section>

        {/* Videos section */}
        <section className="py-16 bg-gray-900">
            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <motion.h2 
                className="text-4xl font-bold text-white text-center mb-12"
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6 }}
                viewport={{ once: true }}
            >
                EXCLUSIVE VIDEOS
            </motion.h2>
    
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
                {mockVideos.map((video, index) => (
                <VideoCard 
                    key={video.id} 
                    video={video} 
                    index={index} 
                    onClick={() => setSelectedVideo(video)}
                />
                ))}
            </div>
    
            {/* Video Modal */}
            {selectedVideo && (
                <VideoModal 
                video={selectedVideo} 
                onClose={() => setSelectedVideo(null)} 
                />
            )}
            </div>
        </section>

        {/* Footer */}
        <footer className="bg-black border-t border-gray-800">
            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
            <div className="grid grid-cols-1 md:grid-cols-4 gap-8">
                {/* Logo and Description */}
                <div className="col-span-1 md:col-span-2">
                <div className="text-white font-bold text-2xl mb-4">
                    MAYEZ<span className="text-red-500">TOURNAMENT</span>
                </div>
                <p className="text-gray-400 mb-6 max-w-md">
                    The premier pool tournament organization, bringing you the most exciting and competitive pool championships worldwide.
                </p>
                <div className="flex space-x-4">
                    <a href="#" className="text-gray-400 hover:text-white transition-colors">
                    <div className="w-8 h-8 bg-gray-700 rounded-full flex items-center justify-center">
                        <span className="text-sm">f</span>
                    </div>
                    </a>
                    <a href="#" className="text-gray-400 hover:text-white transition-colors">
                    <div className="w-8 h-8 bg-gray-700 rounded-full flex items-center justify-center">
                        <span className="text-sm">t</span>
                    </div>
                    </a>
                    <a href="#" className="text-gray-400 hover:text-white transition-colors">
                    <div className="w-8 h-8 bg-gray-700 rounded-full flex items-center justify-center">
                        <span className="text-sm">i</span>
                    </div>
                    </a>
                </div>
                </div>

                {/* Quick Links */}
                <div>
                <h4 className="text-white font-semibold mb-4">Quick Links</h4>
                <ul className="space-y-2">
                    {['Tournaments', 'Players', 'Rules', 'Calendar', 'Live Scoring'].map((link) => (
                    <li key={link}>
                        <a href="#" className="text-gray-400 hover:text-white transition-colors">
                        {link}
                        </a>
                    </li>
                    ))}
                </ul>
                </div>

                {/* Contact */}
                <div>
                <h4 className="text-white font-semibold mb-4">Contact</h4>
                <ul className="space-y-2 text-gray-400">
                    <li>info@mayeztournament.com</li>
                    <li>+1 (555) 123-4567</li>
                    <li>123 Tournament Ave<br />Las Vegas, NV 89101</li>
                </ul>
                </div>
            </div>

            <div className="border-t border-gray-800 pt-8 mt-8">
                <div className="flex flex-col md:flex-row justify-between items-center">
                <p className="text-gray-400 text-sm">
                    © 2025 Mayez Tournament. All rights reserved.
                </p>
                <div className="flex space-x-6 mt-4 md:mt-0">
                    <a href="#" className="text-gray-400 hover:text-white text-sm transition-colors">
                    Privacy Policy
                    </a>
                    <a href="#" className="text-gray-400 hover:text-white text-sm transition-colors">
                    Terms of Service
                    </a>
                    <a href="#" className="text-gray-400 hover:text-white text-sm transition-colors">
                    Cookie Policy
                    </a>
                </div>
                </div>
            </div>
            </div>
        </footer>
    </div>
  )
}

export default Home
